// Manually declare process for environment variables
declare const process: {
  env: {
    [key: string]: string | undefined;
    API_KEY?: string;
  }
};

// Manually declare module types for assets commonly handled by Vite
declare module '*.svg' {
  const content: string;
  export default content;
}

declare module '*.png' {
  const content: string;
  export default content;
}

declare module '*.jpg' {
  const content: string;
  export default content;
}

declare module '*.jpeg' {
  const content: string;
  export default content;
}

declare module '*.gif' {
  const content: string;
  export default content;
}

declare module '*.webp' {
  const content: string;
  export default content;
}
